<script>import GoogleModule from "./google-module";
export let font = null;
export let fonts = null;
export let display = "auto";
export let effect = "none";
export let subsets = [];
export let text = "";
$: styleSheetUrl = GoogleModule.getStylesheet(font !== null && font !== void 0 ? font : fonts, {
    display,
    subsets,
    effect,
    text,
});
</script>

<slot styleSheetUrl="{styleSheetUrl}">
    <link rel="stylesheet" href="{styleSheetUrl}" />
</slot>
